CREATE TABLE IF NOT EXISTS public.purchase_orders (status character varying (20) NOT NULL, notes text , supplier_id integer  NOT NULL, total_amount numeric  NOT NULL, created_at timestamp without time zone , order_date timestamp without time zone  NOT NULL, id integer  NOT NULL, order_number character varying (50) NOT NULL, updated_at timestamp without time zone , delivery_date timestamp without time zone );
CREATE TABLE IF NOT EXISTS public.suppliers (address text  NOT NULL, id integer  NOT NULL, contact_info text  NOT NULL, updated_at timestamp without time zone , created_at timestamp without time zone , name text  NOT NULL);
CREATE TABLE IF NOT EXISTS public.order_items (id integer  NOT NULL, quantity integer  NOT NULL, product_id integer , order_id integer , price numeric  NOT NULL);
CREATE TABLE IF NOT EXISTS public.customer_profiles (photo text , pet_type character varying (10) NOT NULL, name text  NOT NULL, created_at timestamp without time zone , pet_birthday date , email character varying (255) NOT NULL, id integer  NOT NULL, updated_at timestamp without time zone , phone_number character varying (20) NOT NULL, address text  NOT NULL);
CREATE TABLE IF NOT EXISTS public.purchase_order_items (total_price numeric  NOT NULL, created_at timestamp without time zone , product_id integer  NOT NULL, delivered_quantity integer , updated_at timestamp without time zone , quantity integer  NOT NULL, id integer  NOT NULL, purchase_order_id integer  NOT NULL, unit_price numeric  NOT NULL);
CREATE TABLE IF NOT EXISTS public.orders (updated_at timestamp without time zone , customer_profile_id integer , customer_email text  NOT NULL, id integer  NOT NULL, status text  NOT NULL, total numeric  NOT NULL, created_at timestamp without time zone , store_id integer , customer_name text  NOT NULL);
CREATE TABLE IF NOT EXISTS public.inventory (location text , center_id text , inventory_type text  NOT NULL, barcode text , store_id integer , id integer  NOT NULL, supplier_id integer , product_id integer , purchase_date date , updated_at timestamp without time zone , expiry_date date , quantity integer  NOT NULL);
CREATE TABLE IF NOT EXISTS public.brands (name text  NOT NULL, updated_at timestamp without time zone , id integer  NOT NULL, created_at timestamp without time zone , description text );
CREATE TABLE IF NOT EXISTS public.role_types (description text  NOT NULL, id integer  NOT NULL);
CREATE TABLE IF NOT EXISTS public.users (id integer  NOT NULL, role_id integer  NOT NULL, password text  NOT NULL, updated_at timestamp without time zone , created_at timestamp without time zone , username text  NOT NULL);
CREATE TABLE IF NOT EXISTS public.categories (updated_at timestamp without time zone , name text  NOT NULL, description text , id integer  NOT NULL, created_at timestamp without time zone );
CREATE TABLE IF NOT EXISTS public.roles (created_at timestamp without time zone , id integer  NOT NULL, role_type_id integer  NOT NULL, updated_at timestamp without time zone , description text , name text  NOT NULL, permissions jsonb  NOT NULL);
CREATE TABLE IF NOT EXISTS public.stores (name text  NOT NULL, created_at timestamp without time zone , updated_at timestamp without time zone , location text  NOT NULL, contact_info text  NOT NULL, id integer  NOT NULL);
CREATE TABLE IF NOT EXISTS public.products (updated_at timestamp without time zone , name text  NOT NULL, price numeric  NOT NULL, category_id integer  NOT NULL, min_stock integer  NOT NULL, description text , created_at timestamp without time zone , brand_id integer , sku text  NOT NULL, id integer  NOT NULL);
CREATE TABLE IF NOT EXISTS public.user_store_assignments (user_id integer  NOT NULL, updated_at timestamp without time zone , created_at timestamp without time zone , id integer  NOT NULL, store_id integer  NOT NULL);